package org.example;

import javax.swing.*;

public class Main {

    private static int capa = 200; // z 100 = a capa 0
    private static final int columnas = 50; // x
    private static final int filas = 50; // y
    private static String[][][] cuadricula3d = new String[capa][columnas][filas];
    private static final int CAPA_A_MOSTRAR = 100; // Puedes cambiar esto para ver otra capa (e.g., 50)

    public static void main(String[] args) {
        // 1. Crear el modelo de datos
        CubeModel cubeModel = new CubeModel(CAPAS, COLUMNAS, FILAS);
        cubeModel.llenarCubo();

        // 2. Ejecutar la creación de la GUI en el Event Dispatch Thread (EDT)
        SwingUtilities.invokeLater(() -> {
            CubeVisualizer visualizer = new CubeVisualizer(cubeModel.getCuboData(), CAPA_A_MOSTRAR);
            visualizer.setVisible(true);
        });
    }
}

